import React, {useState} from 'react'
import axios from 'axios'
import Mainpicture from '../images/life-satisfaction.svg'
import Logo from '../images/logo.svg'
import {Link, navigate} from '@reach/router'

const Create = (props) => {
    const [formState, setFormState] = useState({
        title:"",
        dueDate:"",
    })
    const [errorState, setErrorState] = useState({
        title: "",
        dueDate: "",
    })

    const titleError = "Project title must be at least 3 characters long."

    const onSubmitHandler = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8000/api/projects/create',formState)
        .then(response => {
            if (response.data.errors) {
                setErrorState({
                    title: response.data.errors.title ? response.data.errors.title.message : "",
                    dueDate: response.data.errors.dueDate ? response.data.errors.dueDate.message : ""
                })
            } else {
                console.log("Success")
                navigate ("/")
            }
        })
        .catch(error=>console.log(error))
    }
    const onChangeHandlder = (event) => {
        setFormState({
            ...formState,
            [event.target.name]:event.target.value
        })
    }
    return (
        <div>
            <h1 style = {{'background-image': 'linear-gradient(to top, #09203f 0%, #537895 100%)', color:'white',padding:'20px',marginBottom:'40px'}}>Project Manager</h1>
            <p><Link to ="/" class="btn btn-secondary">Back to Dashboard</Link></p>
            <div id = "formz" style={{'border': '5px double white', 'background-image': 'linear-gradient(to top, #e6e9f0 0%, #eef1f5 100%)', width:'50%',textAlign:'center',margin:'0 auto',padding:'50px'}}>
                <h2>Plan a New Project</h2>
                <form onSubmit = {onSubmitHandler}>
                    <div class="form-group">
                    <label>Project: </label>
                    <input type ="text" class="form-control" name = "title" onChange= {onChangeHandlder}></input>
                    <small  class="form-text text-muted" style={{color:"red"}}>{errorState.title}</small>
                    <small  class="form-text text-muted" style={{color:"red"}}>{ formState.title.length > 0 && formState.title.length < 3 && titleError}</small>
                    </div>
                    <div class="form-group">
                    <label>Due Date: </label>
                    <input type ="date" class="form-control" name = "dueDate" onChange= {onChangeHandlder}></input>
                    <small  class="form-text text-muted" style={{color:"red"}}>{errorState.dueDate}</small>
                    </div>
                    <p>
                        <button type ="submit" class="btn btn-info">Plan Project</button>
                    </p>
                </form>
            </div>
        </div>
    )
}

export default Register;
